function round(r,e=0){if(e>0)var $=10**e;else if(e<0)var $=.1**(e=Math.abs(e));else $=1;return Math.round(r*$)/$}function roundup(r,e=0){if(e>0)var $=10**e;else if(e<0)var $=.1**(e=Math.abs(e));else $=1;return Math.ceil(r*$)/$}function rounddown(r,e=0){if(e>0)var $=10**e;else if(e<0)var $=.1**(e=Math.abs(e));else $=1;return Math.floor(r*$)/$}